package com.phuonghai.inspection

import androidx.compose.ui.test.junit4.createAndroidComposeRule
import androidx.compose.ui.test.onNodeWithText
import androidx.compose.ui.test.onNodeWithContentDescription
import androidx.compose.ui.test.performClick
import androidx.test.ext.junit.runners.AndroidJUnit4
import com.donut.assignment2.presentation.supervisor.profile.SupervisorProfileScreen
import org.junit.Rule
import org.junit.Test
import org.junit.runner.RunWith
import androidx.navigation.compose.rememberNavController

@RunWith(AndroidJUnit4::class)
class SupervisorProfileScreenTest {
    @get:Rule
    val composeTestRule = createAndroidComposeRule<MainActivity>()

    @Test
    fun profileScreen_displaysProfileInfo() {
        composeTestRule.setContent {
            SupervisorProfileScreen(navController = rememberNavController())
        }
        // Check if the title and Logout button are displayed
        composeTestRule.onNodeWithText("Account").assertExists()
        composeTestRule.onNodeWithText("Logout").assertExists()
    }

    @Test
    fun profileScreen_logoutDialogAppearsOnClick() {
        composeTestRule.setContent {
            SupervisorProfileScreen(navController = rememberNavController())
        }
        // Click the Logout button
        composeTestRule.onNodeWithText("Logout").performClick()
        // Check if the dialog appears
        composeTestRule.onNodeWithText("Sign Out").assertExists()
        composeTestRule.onNodeWithText("Are you sure you want to sign out?").assertExists()
        composeTestRule.onNodeWithText("Cancel").assertExists()
        composeTestRule.onNodeWithText("Sign Out").assertExists()
    }
}
